tiny_rgb = RGBLed(pin16.pin, 4)

event_manager.reset()

def on_event_timer_callback_v_J_E_v_c():
  global Moisture, cmd, LIGHT, mode, TEMP, LIGHT_ON, PUMP_ON
  if mode == 4:
    if LIGHT < 15:
      tiny_rgb.show(0, hex_to_rgb('#ff0000'))
      LIGHT_ON = 1
    else:
      tiny_rgb.show(0, hex_to_rgb('#000000'))
      LIGHT_ON = 0
    if Moisture > 8:
      if TEMP < 38:
        pin14.write_analog(round(translate(70, 0, 100, 0, 1023)))
        PUMP_ON = 2
        time.sleep_ms(5000)
        pin14.write_analog(round(translate(0, 0, 100, 0, 1023)))
        PUMP_ON = 3
  else:
    if PUMP_ON == 2:
      pin14.write_analog(round(translate(70, 0, 100, 0, 1023)))
    if PUMP_ON == 3:
      pin14.write_analog(round(translate(0, 0, 100, 0, 1023)))
    if LIGHT_ON == 1:
      tiny_rgb.show(0, hex_to_rgb('#ff0000'))
    if LIGHT_ON == 0:
      tiny_rgb.show(0, hex_to_rgb('#000000'))

event_manager.add_timer_event(200, on_event_timer_callback_v_J_E_v_c)

aiot_dht20 = DHT20(SoftI2C(scl=Pin(22), sda=Pin(21)))

def on_event_timer_callback_r_s_k_Y_E():
  global Moisture, cmd, LIGHT, mode, TEMP, LIGHT_ON, PUMP_ON
  Moisture = translate((pin0.read_analog()), 0, 4095, 0, 100)
  LIGHT = round(translate((pin1.read_analog()), 0, 4095, 0, 100))
  TEMP = aiot_dht20.dht20_temperature()
  if mode == 4:
    print(('!1:MODE:' + str('E' + '#')), end =' ')
  else:
    print(('!1:MODE:' + str('F' + '#')), end =' ')
  if LIGHT_ON == 1:
    print(('!5:LIGHT_ON:' + str('A' + '#')), end =' ')
  else:
    print(('!5:LIGHT_ON:' + str('B' + '#')), end =' ')
  if PUMP_ON == 2:
    print(('!4:PUMP:' + str('C' + '#')), end =' ')
  else:
    print(('!4:PUMP:' + str('D' + '#')), end =' ')
  print(('!1:TEMP:' + str(str(TEMP) + '#')), end =' ')
  print(('!2:MOISTURE:' + str(str(Moisture) + '#')), end =' ')
  print(('!3:LIGHT:' + str(str(LIGHT) + '#')), end =' ')

event_manager.add_timer_event(20000, on_event_timer_callback_r_s_k_Y_E)

def read_terminal_input():
  spoll=uselect.poll()        # Set up an input polling object.
  spoll.register(sys.stdin, uselect.POLLIN)    # Register polling object.

  input = ''
  if spoll.poll(0):
    input = sys.stdin.read(1)

    while spoll.poll(0):
      input = input + sys.stdin.read(1)

  spoll.unregister(sys.stdin)
  return input

def on_event_timer_callback_I_y_c_Y_b():
  global Moisture, cmd, LIGHT, mode, TEMP, LIGHT_ON, PUMP_ON
  cmd = read_terminal_input()
  if cmd == 'A':
    LIGHT_ON = 1
  if cmd == 'B':
    LIGHT_ON = 0
  if cmd == 'C':
    PUMP_ON = 2
  if cmd == 'D':
    PUMP_ON = 3
  if cmd == 'E':
    mode = 4
  if cmd == 'F':
    mode = 5

event_manager.add_timer_event(200, on_event_timer_callback_I_y_c_Y_b)

